/*eslint no-console:0*/

function printStates(states) {
  states.forEach((state, i) => {
    console.log('===', i, '===');
    state.forEach((earleyItem) => {
      console.log(
        earleyItem.name, '->', earleyItem.rule,
        '(', earleyItem.origin, ')',
        '[', earleyItem.position, ']'
      );
    });
  });
}

function printThings(states, sent, tokenizer) {
  let tokens = (tokenizer)(sent);
  states.forEach((state, i) => {
    state.forEach((earleyItem) => {
      console.log(
        earleyItem.name, ':',
        tokens.slice(i, earleyItem.origin).join(' ')
      );
    });
  });
}

module.exports.printStates = printStates;
module.exports.printThings = printThings;
